//000000000000a:40:{s:2:"id";s:5:"28583";s:4:"body";s:198:"一方水土养一方人，一方人筑一方城。边地城市风貌的千姿百态，原本就是________的事情。<br>填入划横线部分最恰当的一项是（&nbsp;&nbsp;&nbsp;&nbsp;）。";s:7:"bd_desc";s:0:"";s:7:"options";s:141:"[{"cs":"A ", "text":"水到渠成"},{"cs":"B ", "text":"司空见惯"},{"cs":"C ", "text":"顺其自然"},{"cs":"D ", "text":"顺理成章"}]";s:11:"description";s:362:"<p class="item-p">“顺理成章”指写文章或做事情顺着条理就能做好，也比喻某种情况自然产生某种结果。根据题干的逻辑关系，因为“一方水土养一方人，一方人筑一方城”，所以边地城市风貌的千姿百态应是“顺理成章”的事。填入文段符合语境。因此，本题答案为D选项</p>";s:7:"dp_desc";s:0:"";s:6:"answer";s:1:"D";s:8:"wrongans";s:1:"B";s:6:"status";s:1:"1";s:10:"optiontype";s:1:"1";s:8:"bigtype1";s:21:"言语理解与表达";s:10:"smalltype1";s:12:"逻辑填空";s:10:"pointtype1";s:12:"成语辨析";s:5:"tagid";s:1:"0";s:6:"chance";s:1:"0";s:6:"ismock";s:1:"1";s:5:"qyear";s:4:"2009";s:5:"views";s:1:"2";s:6:"rorate";s:3:"250";s:7:"usetime";s:1:"1";s:10:"materialid";s:1:"0";s:4:"diff";s:1:"0";s:7:"addtime";s:1:"0";s:10:"updatetime";s:1:"0";s:10:"supplierid";s:1:"2";s:8:"sourceid";s:5:"33603";s:8:"insertid";s:1:"0";s:6:"extend";s:4:"null";s:5:"skill";s:4:"null";s:7:"bigtype";s:1:"1";s:9:"smalltype";s:1:"7";s:9:"pointtype";s:2:"29";s:6:"tagids";N;s:7:"errnums";s:1:"1";s:8:"collects";s:1:"0";s:5:"marks";s:1:"0";s:5:"notes";s:1:"0";s:4:"bugs";s:1:"0";s:7:"loglist";N;s:9:"pointname";s:12:"成语辨析";}
