//000000000000a:40:{s:2:"id";s:5:"28488";s:4:"body";s:381:"某高校对一些学生进行问卷调查。在接受调查的学生中，准备参加注册会计师考试的有63人，准备参加英语六级考试的有89人，准备参加计算机考试的有47人，三种考试都准备参加的有24人，准备选择两种考试参加的有46人，不参加其中任何一种考试的有15人。问接受调查的学生共有多少人？";s:7:"bd_desc";s:0:"";s:7:"options";s:105:"[{"cs":"A ", "text":"120"},{"cs":"B ", "text":"144"},{"cs":"C ", "text":"177"},{"cs":"D ", "text":"192"}]";s:11:"description";s:583:"<p class="item-p">解法一：设只参加了一种考试的学生有x人，则可得x＋24×3＋46×2＝63＋89＋47，解得x＝35，则至少参加一种考试的学生数为35＋46＋24＝105人，接受调查的学生人数为105＋15＝120人。因此，本题答案选择A选项。</p><p class="item-p">解法二：总共参加的考生人数应为三种考试的人数再减去重复参考的学生人数，因此总共参加考试的学生人数为63＋89＋47－24×2－46=105人，接受调查的学生人数为105＋15＝120人。因此，本题答案选择A选项。</p>";s:7:"dp_desc";s:0:"";s:6:"answer";s:1:"A";s:8:"wrongans";s:0:"";s:6:"status";s:1:"1";s:10:"optiontype";s:1:"1";s:8:"bigtype1";s:12:"数量关系";s:10:"smalltype1";s:12:"数学运算";s:10:"pointtype1";s:15:"三集合容斥";s:5:"tagid";s:1:"0";s:6:"chance";s:1:"0";s:6:"ismock";s:1:"1";s:5:"qyear";s:4:"2010";s:5:"views";s:1:"0";s:6:"rorate";s:1:"0";s:7:"usetime";s:1:"0";s:10:"materialid";s:1:"0";s:4:"diff";s:1:"0";s:7:"addtime";s:1:"0";s:10:"updatetime";s:1:"0";s:10:"supplierid";s:1:"2";s:8:"sourceid";s:5:"55310";s:8:"insertid";s:1:"0";s:6:"extend";s:4:"null";s:5:"skill";s:34:"<p class="item-p">列方程法</p>";s:7:"bigtype";s:1:"2";s:9:"smalltype";s:2:"68";s:9:"pointtype";s:2:"90";s:6:"tagids";N;s:7:"errnums";s:1:"0";s:8:"collects";s:1:"0";s:5:"marks";s:1:"0";s:5:"notes";s:1:"0";s:4:"bugs";s:1:"0";s:7:"loglist";N;s:9:"pointname";s:12:"容拆原理";}
