//000000000000a:40:{s:2:"id";s:5:"29881";s:4:"body";s:166:"20世纪90年代初出现的“新经济”是相对于传统经济而言的一种经济模式。下列能表达其基本特征的有（&nbsp;&nbsp;&nbsp;&nbsp;）。";s:7:"bd_desc";s:0:"";s:7:"options";s:129:"[{"cs":"A ", "text":"全球化"},{"cs":"B ", "text":"市场化"},{"cs":"C ", "text":"信息化"},{"cs":"D ", "text":"专业化"}]";s:11:"description";s:439:"<p class="item-p">所谓“新经济”是建立在信息技术革命和制度创新基础上的经济持续增长与低通货膨胀率、低失业率并存，经济周期的阶段性特征明显淡化的一种新的经济现象。“全球化”是新经济产生的背景而不是其作为一种经济模式自身的基本特征，而市场化，信息化，专业化都是新经济自身的基本特征。因此，本题选择BCD项</p>";s:7:"dp_desc";s:0:"";s:6:"answer";s:3:"BCD";s:8:"wrongans";s:0:"";s:6:"status";s:1:"1";s:10:"optiontype";s:1:"3";s:8:"bigtype1";s:12:"常识判断";s:10:"smalltype1";s:6:"经济";s:10:"pointtype1";s:18:"热点经济词汇";s:5:"tagid";s:1:"0";s:6:"chance";s:1:"0";s:6:"ismock";s:1:"1";s:5:"qyear";s:4:"2003";s:5:"views";s:1:"0";s:6:"rorate";s:1:"0";s:7:"usetime";s:1:"0";s:10:"materialid";s:1:"0";s:4:"diff";s:1:"0";s:7:"addtime";s:1:"0";s:10:"updatetime";s:1:"0";s:10:"supplierid";s:1:"2";s:8:"sourceid";s:5:"39026";s:8:"insertid";s:1:"0";s:6:"extend";s:4:"null";s:5:"skill";s:55:"<p class="item-p">排除法，全球化是时代背</p>";s:7:"bigtype";s:1:"5";s:9:"smalltype";s:3:"104";s:9:"pointtype";s:3:"149";s:6:"tagids";s:3:"207";s:7:"errnums";s:1:"0";s:8:"collects";s:1:"0";s:5:"marks";s:1:"0";s:5:"notes";s:1:"0";s:4:"bugs";s:1:"0";s:7:"loglist";N;s:9:"pointname";s:18:"热点经济词汇";}
