//000000000000a:40:{s:2:"id";s:5:"28486";s:4:"body";s:254:"一公司销售部有4名区域销售经理，每人负责的区域数相同，每个区域都正好有两名销售经理负责，而任意两名销售经理负责的区域只有一个相同。问这4名销售经理总共负责多少个区域的业务？";s:7:"bd_desc";s:0:"";s:7:"options";s:98:"[{"cs":"A ", "text":"4"},{"cs":"B ", "text":"6"},{"cs":"C ", "text":"8"},{"cs":"D ", "text":"12"}]";s:11:"description";s:348:"每个区域都只由两名销售经理负责，且任意两名销售经理负责的区域只有一个相同，从4个经理中一共能挑出&nbsp;<img src="/Uploads/xingce/desc2/11d8afa6be1d4295f91de8e1281f3100_0.png" /> 种组合，每种组合对应一个区域，则名经理共负责6个区域的业务。因此，本题答案选择B选项。";s:7:"dp_desc";s:38:"11d8afa6be1d4295f91de8e1281f3100_0.png";s:6:"answer";s:1:"B";s:8:"wrongans";s:0:"";s:6:"status";s:1:"1";s:10:"optiontype";s:1:"1";s:8:"bigtype1";s:12:"数量关系";s:10:"smalltype1";N;s:10:"pointtype1";s:15:"基础公式型";s:5:"tagid";s:1:"0";s:6:"chance";s:1:"0";s:6:"ismock";s:1:"1";s:5:"qyear";s:4:"2010";s:5:"views";s:1:"0";s:6:"rorate";s:1:"0";s:7:"usetime";s:1:"0";s:10:"materialid";s:1:"0";s:4:"diff";s:1:"0";s:7:"addtime";s:1:"0";s:10:"updatetime";s:1:"0";s:10:"supplierid";s:1:"2";s:8:"sourceid";s:5:"55308";s:8:"insertid";s:1:"0";s:6:"extend";s:4:"null";s:5:"skill";s:4:"null";s:7:"bigtype";s:1:"2";s:9:"smalltype";s:1:"0";s:9:"pointtype";s:1:"0";s:6:"tagids";N;s:7:"errnums";s:1:"0";s:8:"collects";s:1:"0";s:5:"marks";s:1:"0";s:5:"notes";s:1:"0";s:4:"bugs";s:1:"0";s:7:"loglist";N;s:9:"pointname";N;}
